CREATE TABLE makia.entries ( id INT AUTO_INCREMENT PRIMARY KEY, timestamp DATETIME, locationID INT, direction ENUM('INCOMING', 'OUTGOING'), category ENUM('CAR', 'TRUCK', 'MOTORCYCLE', 'BUS', 'TRACTOR', 'TRAIN'), stoppedForMs INT, avgVelocity DECIMAL(5,2), minVelocity DECIMAL(5,2), stoppedDistance DECIMAL(5,2), convoyIndex INT, convoyType ENUM('CAR', 'TRUCK', 'MOTORCYCLE', 'BUS', 'TRACTOR', 'TRAIN'));
